(74, 1, 0, 'cartTpl', '', 0, 12, 0, '<div id="msCart">\n<h1 class="pageTitle">{''ms2_cart'' | lexicon}</h1>\n    {if !count($products)}\n        {''ms2_cart_is_empty'' | lexicon}\n    {else}\n		<table class="cart-table full table--responsive">\n			<thead class="cart__row cart__header-labels">\n				<tr>\n				    <th class="image">&nbsp;</th>\n					<th class="text-center">{''ms2_cart_title'' | lexicon}</th>\n					<th class="text-center">{''ms2_cart_count'' | lexicon}</th>\n					<!-- <th class="text-center">{''ms2_cart_weight'' | lexicon}</th> -->\n					<th class="text-center">{''ms2_cart_price'' | lexicon}</th>\n					<th class="text-center">{''ms2_cart_remove'' | lexicon}</th>\n				</tr>\n			</thead>\n			<tbody>\n			    {foreach $products as $product}\n				<tr class="cart__row" id="{$product.key}">\n					<td class="image">\n					    {if $product.thumb?}\n                            <img src="{$product.thumb}" alt="{$product.pagetitle}" title="{$product.pagetitle}"/>\n                        {else}\n                            <img src="{''assets_url'' | option}components/minishop2/img/web/ms2_small.png"\n                                 srcset="{''assets_url'' | option}components/minishop2/img/web/ms2_small@2x.png 2x"\n                                 alt="{$product.pagetitle}" title="{$product.pagetitle}"/>\n                        {/if}\n					 </td>\n					<td class="title">\n					    {if $product.id?}\n                            <a href="{$product.id | url}">{$product.pagetitle}</a>\n                        {else}\n                            {$product.name}\n                        {/if}\n                        {if $product.options?}\n                            <!--<div class="small">-->\n                            <!--    {$product.options | join : ''; ''}-->\n                            <!--</div>-->\n                        {/if}\n					</td>\n					<td data-label="Quantity" class="count qty">\n    				    <form method="post" class="ms2_form form-inline" role="form">\n                            <input type="hidden" name="key" value="{$product.key}"/>\n                            <div class="input-group spinner number">\n                                <button class="spinbtn btnminus" type="button"><span aria-hidden="true" class="icon icon-minus"></span></button>\n                                <input  name="count" value="{$product.count}" class="txt-qty"/>\n                                <button class="spinbtn btnplus" type="button"><span aria-hidden="true" class="icon icon-plus"></span></button>\n                            </div>\n                            <button class="btn refresh" type="submit" name="ms2_action" value="cart/change">\n                               <i class="fa fa-refresh" aria-hidden="true"></i>\n                            </button>\n                        </form>\n					</td>\n					<!--<td data-label="Price" class="weight price">-->\n     <!--                   <span>{$product.weight}</span> указать еденицу веса}-->\n     <!--               </td>-->\n                    <td data-label="Price" class="price">\n                        <span>{$product.price}</span> грн<!--тут можно будет указать валюту товара -->\n                        {if $product.old_price?}\n                        <span class="old_price">{$product.old_price}</span> грн<!--тут можно будет указать валюту товара -->\n                            {/if}\n                    </td>\n                     <td class="remove">\n                        <form method="post" class="ms2_form">\n                            <input type="hidden" name="key" value="{$product.key}">\n                            <button class="btn btn-default" type="submit" name="ms2_action" value="cart/remove">\n                                <i class="fa fa-times" aria-hidden="true"></i>\n                            </button>\n                        </form>\n                    </td>\n                    </tr>\n                {/foreach}\n                <tr class="footer">\n                    <th class="total" colspan="2">{''ms2_cart_total'' | lexicon}:</th>\n                    <th class="total_count">\n                        <span class="ms2_total_count">{$total.count}</span>\n                        {''ms2_frontend_count_unit'' | lexicon} \n                    </th>\n                    <!-- <th class="total_weight">\n                    <span class="ms2_total_weight">{$total.weight}</span>\n                    </th> -->\n                    <th class="total_cost">\n                        <span class="ms2_total_cost">{$total.cost}</span> грн\n                        <!--тут можно будет указать валюту товара -->\n                    </th>\n                    <th>&nbsp;</th>\n                </tr>\n            </tbody>\n    </table>\n				<form method="post">\n            <button class="btn btn-default" type="submit" name="ms2_action" value="cart/clean">\n               <i class="fa fa-times" aria-hidden="true"></i> {''ms2_cart_clean'' | lexicon}\n            </button>\n        </form>\n    {/if}\n</div>', 0, 'a:0:{}', 0, ''),
(75, 0, 0, 'cartTplMobile', '', 0, 12, 0, '<div id="msCart">\n    {if !count($products)}\n        {''ms2_cart_is_empty'' | lexicon}\n    {else}\n    <div id="CartDrawer" class="drawer drawer--right">\n    	<div class="drawer__header">\n    		<div class="drawer__title h3">{''ms2_cart'' | lexicon}</div>\n    		<div class="drawer__close js-drawer-close">\n    			<button type="button"><span class="icon icon-x" aria-hidden="true"></span></button>\n    		</div>\n    	</div>\n    	<div id="CartContainer">\n		    {foreach $products as $product}\n			<div class="ajaxcart__product" id="{$product.key}">\n				<div class="row">\n					<div class="col-xs-4">\n					     {if $product.thumb?}\n					     <a class="ajaxcart__product-image" href="{$product.id | url}">\n                            <img src="{$product.thumb}" alt="{$product.pagetitle}" title="{$product.pagetitle}"/>\n                         </a>\n                        {else}\n                            <a class="ajaxcart__product-image" href="{$product.id | url}"><img src="{''assets_url'' | option}components/minishop2/img/web/ms2_small.png"\n                                 srcset="{''assets_url'' | option}components/minishop2/img/web/ms2_small@2x.png 2x"\n                                 alt="{$product.pagetitle}" title="{$product.pagetitle}"/></a>\n                        {/if}\n				    </div>\n					<div class="col-xs-8">\n						<p> \n						{if $product.id?}\n                            <a class="ajaxcart__product-name" href="{$product.id | url}">{$product.pagetitle}</a>\n                        {else}\n                            {$product.name}\n                        {/if}\n                        {if $product.options?}\n                            <div class="small">\n                            {$product.options | join : ''; ''}\n                            </div>\n                        {/if}\n					    </p>\n						<div class="row-table">\n							<div class="row-cell col-xs-7 count">\n								 <form method="post" class="ms2_form form-inline miniCart" role="form">\n                                    <input type="hidden" name="key" value="{$product.key}"/>\n                                    <div class="input-group spinner number">\n                                            <input  name="count" value="{$product.count}" class="miniCartCount"/>\n                                    </div>\n                                    <button class="btn refresh" type="submit" name="ms2_action" value="cart/change">\n                                       <i class="fa fa-refresh" aria-hidden="true"></i>\n                                    </button>\n                                </form>\n                                <form method="post" class="ms2_form miniCart">\n                                    <input type="hidden" name="key" value="{$product.key}">\n                                    <button class="btn btn-default" type="submit" name="ms2_action" value="cart/remove">\n                                        <i class="fa fa-times" aria-hidden="true"></i>\n                                    </button>\n                                </form>\n							</div>\n							<div class="row-cell col-xs-5 text-right"> \n							    <span>{$product.price}</span> грн\n                                    {if $product.old_price?}\n                                        <span class="old_price">{$product.old_price}</span> грн\n                                    {/if}\n							</div>\n						</div>\n					</div>\n				</div>\n			</div>\n			{/foreach}\n			<div class="ajaxcart__footer">\n				<div class="row">\n					<div class="col-sm-7">\n						<p><strong>Общая сумма товара</strong></p>\n					</div>\n					<div class="col-sm-5 text-right">\n						<p><span class="ms2_total_cost">{$total.cost}</span></p>\n					</div>\n				</div>\n				<a class="btn btn--full view-cart" href="[[~21]]">Перейти в корзину</a>\n			</div>\n    	</div>\n    </div>\n    {/if}\n    </div>    ', 0, 'a:0:{}', 0, ''),
(79, 1, 0, 'gridProductTpl', '', 0, 12, 0, '<div class="grid_item col-xs-12 col-sm-6 col-md-4 col-lg-3">\n	<form method="post" class="ms2_form">\n	    [[+stock:is=`нет в наличии`:then=`\n	    <div class="product_image"> \n			<span class="not-available">Нет в наличии</span>\n		</div>\n	    `]]\n    	<div class="product_image"> \n    	{if $thumb?}\n                <a href="{$id | url}"><img src="{$thumb}" alt="{$pagetitle}" title="{$pagetitle}"/></a>\n            {else}\n                <a href="{$id | url}"><img src="{''assets_url'' | option}components/minishop2/img/web/ms2_small.png"\n                     srcset="{''assets_url'' | option}components/minishop2/img/web/ms2_small@2x.png 2x"\n                     alt="{$pagetitle}" title="{$pagetitle}"/></a>\n       {/if}\n       \n        [[+stock:is=`в наличии`:then=`\n        <div class="action-buttons"> \n    		<a class="btn-action wishlist" href="wishlist.html" title="Add To Wishlist">\n    		    <i class="fa fa-heart" aria-hidden="true"></i>\n    		</a>\n    		\n    		<button class="btn-action add-to-cart" type="submit" name="ms2_action" value="cart/add">\n               <span class="icon icon-cart" aria-hidden="true"></span>\n            </button>\n            \n    		<button type="button" class="btn-action btn-quickview" title="Quick View" data-toggle="modal" data-target="#quickView">\n    			    <i class="fa fa-eye" aria-hidden="true"></i>\n    		</button>\n    		\n    		\n    		<input type="hidden" name="id" value="{$id}">\n            <input type="hidden" name="count" value="1">\n            <input type="hidden" name="options" value="[]">\n    		</div>\n        `:else=``]]\n    		\n    	</div>\n    	<div class="product_detail">\n    	    <h3 class="product_name"><a href="{$id | url}">{$pagetitle}</a></h3>\n    		<p> \n    		 <span class="flags">\n    		    <span class="product_price old_price">\n    		        <s>\n    		          {if $old_price?}\n                        <span class="old_price money">{$old_price} {''грн.'' | lexicon}</span>\n                    {/if}\n    		        </s>\n    		    </span>\n    		    {if $new?}\n    			    <label class="new">Новинка</label>\n    			{/if}\n    			<span class="product_price sale_price">\n    			{if $popular?}    \n        			<label class="on-sale">ХИТ</label>\n        		{/if}	\n        			<span class="price"> {$price} {''грн.'' | lexicon}</span>\n    			</span>\n    		 </span>\n    		</p>\n    	</div>\n	</form>\n</div>\n', 0, 'a:0:{}', 0, ''),
(80, 1, 0, 'listProductTpl', '', 0, 12, 0, '<div class="products">\r\n	<div class="products">\r\n	    <form method="post" class="ms2_form">\r\n    		<div class="products-grid listview">\r\n    			<div class="grid_item full-width">\r\n    				<div class="row row-table">\r\n    					{if $new?}\r\n            			    <label class="new">Новинка</label>\r\n            			{/if}\r\n            			[[+stock:is=`в наличии`:then=`\r\n            			<div class="product_image">\r\n                			{if $thumb?}\r\n                                <a href="{$id | url}"><img src="{$thumb}" alt="{$pagetitle}" title="{$pagetitle}"/></a>\r\n                            {else}\r\n                                <a href="{$id | url}"><img src="{''assets_url'' | option}components/minishop2/img/web/ms2_small.png" srcset="{''assets_url'' | option}components/minishop2/img/web/ms2_small@2x.png 2x" alt="{$pagetitle}" title="{$pagetitle}"/></a>\r\n                            {/if}\r\n            				<div class="action-buttons"> \r\n            				    <a class="btn-action wishlist" href="wishlist.html" title="Add To Wishlist">\r\n                        		    <i class="fa fa-heart" aria-hidden="true"></i>\r\n                        		</a>\r\n            				    \r\n            				    <button class="btn-action add-to-cart" type="submit" name="ms2_action" value="cart/add">\r\n                                   <span class="icon icon-cart" aria-hidden="true"></span>\r\n                                </button>\r\n                                \r\n                				<button type="button" class="btn-action btn-quickview" title="Quick View" data-toggle="modal" data-target="#quickView">\r\n                        			    <i class="fa fa-eye" aria-hidden="true"></i>\r\n                        		</button>\r\n                        		\r\n                    			<input type="hidden" name="id" value="{$id}">\r\n                                <input type="hidden" name="count" value="1">\r\n             