ackages/install''\n            ,signature: r.signature\n            ,register: ''mgr''\n            ,topic: topic\n        });\n\n        MODx.Ajax.request({\n            url: MODx.config.connector_url\n            ,params: va\n            ,listeners: {\n                ''success'': {fn:function() {\n                    var bc = Ext.getCmp(''packages-breadcrumbs'');\n                    var trail= bc.data.trail;\n                    trail.pop();\n\n                    if (trail.length > 1) {\n                        last = trail[trail.length - 1];\n\n                        if (last != undefined && last.rec != undefined) {\n                            bc.data.trail.pop();\n                            bc.data.trail.shift();\n                            bc.updateDetail(bc.data);\n\n                            var grid = Ext.getCmp(''modx-package-grid'');\n                            grid.install(last.rec);\n                            return;\n                        }\n                    }\n\n                    this.activate();\n					Ext.getCmp(''modx-package-grid'').refresh();\n                },scope:this}\n                ,''failure'': {fn:function() {\n                    this.activate();\n                },scope:this}\n            }\n        });\n\n        return true;\n	}";s:8:"disabled";s:5:"false";s:5:"scope";s:15:"[object Object]";s:9:"autoWidth";s:4:"true";s:10:"autoHeight";s:4:"true";s:8:"minWidth";s:2:"75";s:10:"removeMode";s:9:"container";s:10:"hideParent";s:4:"true";s:6:"events";s:15:"[object Object]";s:7:"ownerCt";s:15:"[object Object]";s:9:"container";s:15:"[object Object]";s:8:"rendered";s:4:"true";s:8:"template";s:15:"[object Object]";s:5:"btnEl";s:15:"[object Object]";s:4:"mons";s:15:"[object Object]";s:2:"el";s:15:"[object Object]";s:4:"icon";s:0:"";s:7:"iconCls";s:0:"";s:8:"boxReady";s:4:"true";s:11:"useSetClass";s:4:"true";s:6:"oldCls";s:12:"x-btn-noicon";s:3:"doc";s:15:"[object Object]";s:19:"monitoringMouseOver";s:4:"true";s:6:"action";s:26:"workspace/packages/install";s:8:"register";s:3:"mgr";s:5:"topic";s:45:"/workspace/package/install/ajaxform-1.1.7-pl/";s:8:"lastSize";s:15:"[object Object]";s:14:"package_action";i:0;}', 'AjaxForm', 'a:38:{s:2:"id";s:24:"578f8d20f05e8bd318996b02";s:7:"package";s:24:"5279c6d462cf24078e000edb";s:12:"display_name";s:17:"ajaxform-1.1.7-pl";s:4:"name";s:8:"AjaxForm";s:7:"version";s:5:"1.1.7";s:13:"version_major";s:1:"1";s:13:"version_minor";s:1:"1";s:13:"version_patch";s:1:"7";s:7:"release";s:2:"pl";s:8:"vrelease";s:2:"pl";s:14:"vrelease_index";s:1:"0";s:6:"author";s:9:"bezumkin2";s:11:"description";s:193:"<p>1.1.7-pl ==============</p><p>- Fixed possible blind SQL injection in action file.</p><p>1.1.6-pl ==============&nbsp;</p><p>- &#91;#33&#93; Improved the processing of javascript errors.</p>";s:12:"instructions";s:427:"<p>1. Create new chunk with name "myForm".</p><p>2. Add form into that chunk.</p><p>3. Call AjaxForm at any page &#91;&#91;!AjaxForm?form=`myForm`&amp;snippet=`FormIt`&#93;&#93;</p><p></p><p>You can specify any parameters for end snippet:</p><p>&#91;&#91;!AjaxForm?</p><p>	&amp;form=`myForm`</p><p>	&amp;snippet=`FormIt`</p><p>	&amp;hooks=`email`</p><p>	&amp;emailto=`info@mysite.com`</p><p>	&amp;etc=`...`</p><p>&#93;&#93;</p>";s:9:"changelog";s:200:"<p><a href="https://github.com/bezumkin/AjaxForm/blob/master/core/components/ajaxform/docs/changelog.txt" title="" target="_blank">Changelog on GitHub</a></p><p></p><p></p><p></p><p></p><p></p><p></p>";s:9:"createdon";s:24:"2016-07-20T14:39:28+0000";s:9:"createdby";s:9:"bezumkin2";s:8:"editedon";s:24:"2017-04-15T15:43:56+0000";s:10:"releasedon";s:24:"2016-07-20T14:39:28+0000";s:9:"downloads";s:5:"22764";s:8:"approved";s:4:"true";s:7:"audited";s:5:"false";s:8:"featured";s:5:"false";s:10:"deprecated";s:5:"false";s:7:"license";s:5:"GPLv2";s:7:"smf_url";s:0:"";s:10:"repository";s:24:"4d4c3fa6b2b0830da9000001";s:8:"supports";s:3:"2.2";s:8:"location";s:60:"http://modx.com/extras/download/?id=578f8d20f05e8bd318996b